public class A {
	private int a=10;
	int b=20;
	protected int c=30;
	public int d=40;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		A aobj=new A();
		System.out.println(aobj.a);
		System.out.println(aobj.b);
		System.out.println(aobj.c);
		System.out.println(aobj.d);
	}

}
